INSERT INTO `heroes` (`Hero`, `Role`, `Bio`, `Health`, `Armor`, `Shield`, `Difficulty`) VALUES ('Brigitte', 'Support', 'Brigitte specializes in armor. She can throw Repair Packs to heal teammates, or automatically heal nearby allies when she damages foes with her Flail. Her Flail is capable of a wide swing to strike multiple targets, or a Whip Shot that stuns an enemy at range. When entering the fray, Barrier Shield provides personal defense while she attacks enemies with Shield Bash. Brigitte’s ultimate ability, Rally, gives her a substantial short-term boost of speed and provides long-lasting armor to all her nearby allies.', '200', '50', '0', '1');
INSERT INTO `abilities` (`Hero`, `Ability`, `Description`) VALUES ('Brigitte', 'Rocket Flail', 'Brigitte\'s melee weapon has an extended range, enabling her to strike multiple enemies with a single swing.'), ('Brigitte', 'Repair Pack', 'Brigitte throws a Repair Pack that can heal an ally. Any healing over that ally’s maximum health provides them with armor instead.'), ('Brigitte', 'Whip Shot', 'Brigitte throws her flail a long distance, dealing damage and knocking an enemy away from her.'), ('Brigitte', 'Barrier Shield', 'Brigitte deploys a frontal energy barrier to absorb a limited amount of damage.'), ('Brigitte', 'Shield Bash', 'Once her Barrier Shield is deployed, Brigitte can dash forward to stun an enemy.');
INSERT INTO `ultimates` (`Hero`, `Ability`, `Description`) VALUES ('Brigette', 'Rally', 'Brigitte moves faster and provides all nearby allies with armor that lasts until it’s removed by damage.');
